	<$list filter=<<list-composer gsd_type:$gsd_type$ gsd_status:$gsd_status$ gsd_complete:$gsd_complete$ realmAware:"false" sort:$sort$ order:$order$ groupBy:$groupBy$ groupTail:"false" groupTailHeader:"false" groupHeader:"true" customFilter:$customFilter$ ownerField:$ownerField$>>>
		<div class="tc-menu-list-item  gsd5-project-list">
		<<view-composer groupTitle:"$groupBy$" dateTemplate:"$dateTemplate$">>
		<$list filter=<<list-composer gsd_type:$gsd_type$ gsd_status:$gsd_status$ gsd_complete:$gsd_complete$ realmAware:"false" sort:$sort$ order:$order$ groupBy:$groupBy$ groupTail:"false" groupTailHeader:"false" groupHeader:"false" customFilter:$customFilter$ ownerField:$ownerField$>>>
			<div class="tc-menu-list-subitem">
				<$transclude tiddler="$:/plugins/gsd5/core/ui/lists/ListViewPrefix"/>
				<$link to={{!!title}}><$view field="title"/></$link>
