        "$:/plugins/gsd5/core/macro/view-composer.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/macro/view-composer.js
type: application/javascript
module-type: macro

Macro that selects the correct view widget based upon grouping type.  Used with the filter-composer macro.


\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.name = "view-composer";

exports.params = [
	{name: "groupTitle"},
	{name: "dateTemplate"}
];

// Run the macro
exports.run = function(groupTitle, dateTemplate) {

	var templateString="",
		linkString='&nbsp;<span style="font-size: 12px; line-height: 18px;"><$link to=<<currentTiddler>>>>></$link></span>';

	// Process values to resolve any wikitext.
	groupTitle = $tw.wiki.renderText("text/plain","text/vnd.tiddlywiki",groupTitle);
	dateTemplate = $tw.wiki.renderText("text/plain","text/vnd.tiddlywiki",dateTemplate);

	if(groupTitle==="modified"||groupTitle==="created"||groupTitle==="gsd_comp_date") {
		templateString = ' format="date" template="' + dateTemplate + '" ';
		linkString = "";
	}

	return '<$view field="title"' + templateString + ' />' + linkString;
};

})();
",
            "title": "$:/plugins/gsd5/core/macro/view-composer.js",
            "type": "application/javascript",
            "module-type": "macro"
        },
        "$:/plugins/gsd5/core/modules/filters/datefilter.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/modules/filters/datefilter.js
type: application/javascript
module-type: filteroperator

Filter operator for comparing date with date field.

\*/
(function(){
// jslint node: true, browser: true
// global $tw: false
"use strict";
// Export our filter function
exports.date = function(source,operator,options) {
    var results = [],
        fieldname = (operator.suffix || "created").toLowerCase();
    if(operator.prefix === "!") {
        source(function(tiddler,title) {
            if(tiddler) {
                var field_string = tiddler.getFieldString(fieldname);
                var field_object = $tw.utils.parseDate(field_string);
                if(operator.operand.split("-").length === 1) {
                    var now = new Date();
                    now.setUTCHours(0);
                    now.setUTCMinutes(0);
                    now.setUTCSeconds(0);
                    now.setUTCMilliseconds(0);
                    var offsetDays = parseInt(operator.operand) * 86400000;
                    var test_object = new Date(now - offsetDays);
                } else {
                    var test_object = new Date(operator.operand);
                }
                if(isNaN(field_object.valueOf()) === false && isNaN(test_object.valueOf()) === false && field_object <= test_object) {
                    results.push(title);
                }
            }
        });
    } else {
        source(function(tiddler,title) {
            if(tiddler) {
                var field_string = tiddler.getFieldString(fieldname);
                var field_object = $tw.utils.parseDate(field_string);
                if(operator.operand.split("-").length === 1) {
                    var now = new Date();
                    now.setUTCHours(0);
                    now.setUTCMinutes(0);
                    now.setUTCSeconds(0);
                    now.setUTCMilliseconds(0);
                    var offsetDays = parseInt(operator.operand) * 86400000
                    var test_object = new Date(now-offsetDays);
                } else {
                    var test_object = new Date(operator.operand);
                }
                if(isNaN(field_object.valueOf()) === false && isNaN(test_object.valueOf()) === false && field_object >= test_object) {
                    results.push(title);
                }
            }
        });
    }
    return results;
};

})();
",
            "title": "$:/plugins/gsd5/core/modules/filters/datefilter.js",
            "type": "application/javascript",
            "module-type": "filteroperator"
        },
