        "$:/plugins/gsd5/core/modules/widgets/complete.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/modules/widgets/complete.js
type: application/javascript
module-type: widget

Marks an action or project complete and queues the next dependent action.
\*/
(function() {

//jslint node: true, browser: true
//global $tw: false
"use_strict";

var Widget = require("$:/core/modules/widgets/widget.js").widget;

var CompleteWidget = function(parseTreeNode, options) {
  this.initialise(parseTreeNode, options);
};

CompleteWidget.prototype = new Widget();

CompleteWidget.prototype.render = function(parent, nextSibling) {
  var self = this;
  // Save the parent dom node
  this.parentDomNode = parent;
  // Compute our attributes
  this.computeAttributes();
  // Execute our logic
  this.execute();
  // Create our elements
  this.labelDomNode = this.document.createElement("label");
  this.inputDomNode = this.document.createElement("input");
  this.inputDomNode.setAttribute("type","checkbox");
  this.inputDomNode.setAttribute("class","tc-complete-box");
  if(this.getValue()) {
    this.inputDomNode.setAttribute("checked","true");
  }
  this.labelDomNode.appendChild(this.inputDomNode);
  this.spanDomNode = this.document.createElement("span");
  this.labelDomNode.appendChild(this.spanDomNode);
  // Add a click event handler
  this.inputDomNode.addEventListener("change", function() {
    self.handleChangeEvent();
  });
  // Insert the label into the DOM and render any children
  parent.insertBefore(this.labelDomNode,nextSibling);
  this.renderChildren(this.spanDomNode,null);
  this.domNodes.push(this.labelDomNode);
};

CompleteWidget.prototype.getValue = function() {
  var tiddler = this.wiki.getTiddler(this.completeTitle);
  if(tiddler.fields.gsd_complete === "true") {
    return true;
  }
  return false;
};

CompleteWidget.prototype.handleChangeEvent = function() {
  this.wiki.setText(this.completeTitle, "gsd_complete", null, this.inputDomNode.checked.toString());
  var now = new Date();
  this.wiki.setText(this.completeTitle, "gsd_comp_date", null, $tw.utils.stringifyDate(now));
  this.changeDependants();
  $tw.rootWidget.dispatchEvent({type: "tm-auto-save-wiki"});
};

CompleteWidget.prototype.changeDependants = function() {
  var self = this;
  if(this.inputDomNode.checked === true) {
    this.wiki.forEachTiddler(function(title, tiddler) {
      if(tiddler.fields.gsd_status === "future" && tiddler.fields.gsd_action === self.completeTitle) {
        this.setText(title, "gsd_status", null, "next");
      }
    });
  }
};

CompleteWidget.prototype.execute = function() {
  this.completeTitle = this.getAttribute("tiddler",this.getVariable("currentTiddler"));
  this.makeChildWidgets();
};

CompleteWidget.prototype.refresh = function(changedTiddlers) {
  var changedAttributes = this.computeAttributes();
  if(changedAttributes.tiddler || changedAttributes.checked || changedAttributes.unchecked) {
    this.refreshSelf();
    return true;
  } else {
    var refreshed = false;
    if(changedTiddlers[this.completeTitle]) {
      this.inputDomNode.checked = this.getValue();
      refreshed = true;
    }
    return this.refreshChildren(changedTiddlers) || refreshed;
  }
};

exports.complete = CompleteWidget;

})();
",
            "title": "$:/plugins/gsd5/core/modules/widgets/complete.js",
            "type": "application/javascript",
            "module-type": "widget"
        },
        "$:/plugins/gsd5/core/modules/widgets/convert.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/modules/widgets/convert.js
type: application/javascript
module-type: widget

Convert Action to Project widget

\*/
(function(){

// slint node: true, browser: true
// global $tw: false
"use strict";
var Widget = require("$:/core/modules/widgets/widget.js").widget;
var ConvertWidget = function(parseTreeNode,options) {
    this.initialise(parseTreeNode,options);
};

ConvertWidget.prototype = new Widget();

ConvertWidget.prototype.render = function(parent,nextSibling) {
    var self = this;
    // Remember parent
    this.parentDomNode = parent;
    // Compute attributes and execute state
    this.computeAttributes();
    this.execute();
    // Create element
    var domNode = this.document.createElement("button");
    // Assign classes
    var classes = this["class"].split(" ") || [];
    if(this.selectedClass) {
        if(this.set && this.setTo && this.isSelected()) {
            $tw.utils.pushTop(classes,this.selectedClass.split(" "));
        }
        if(this.popup && this.isPoppedUp()) {
            $tw.utils.pushTop(classes,this.selectedClass.split(" "));
        }
    }
    domNode.className = classes.join(" ");
    // Assign other attributes
    if(this.style) {
        domNode.setAttribute("style",this.style);
    }
    if(this.tooltip) {
        domNode.setAttribute("title",this.tooltip);
    }
    if(this["aria-label"]) {
        domNode.setAttribute("aria-label",this["aria-label"]);
    }
    // Add a click event handler
    domNode.addEventListener("click",function (event) {
        self.handleClickEvent();
    });
    // Insert element
    parent.insertBefore(domNode,nextSibling);
    this.renderChildren(domNode,null);
    this.domNodes.push(domNode);
};

ConvertWidget.prototype.handleClickEvent = function() {
    var tiddler = this.wiki.getTiddler(this.to);
    // Check if tiddler is an action.and not completed.
    if(tiddler.fields.gsd_type === "action" && tiddler.fields.gsd_complete === "false") {
        // Change next actions to active projects, others to future.
        if(tiddler.fields.gsd_status === "next") {
            this.wiki.setText(this.to, "gsd_status", null, "active");
        } else {
            this.wiki.setText(this.to, "gsd_status", null, "future");
        }
        // Remove previous dependencies.
        if(tiddler.fields.gsd_action) {
            this.wiki.setText(this.to, "gsd_action", null, "");
        }
        // Finally, change the type to project.
        this.wiki.setText(this.to, "gsd_type", null, "project");
    }
};

ConvertWidget.prototype.execute = function() {
    // Get attributes
    this.to = this.getAttribute("to",this.getVariable("currentTiddler"));
    this["class"] = this.getAttribute("class","");
    this["aria-label"] = this.getAttribute("aria-label");
    this.tooltip = this.getAttribute("tooltip");
    this.style = this.getAttribute("style");
    this.selectedClass = this.getAttribute("selectedClass");
    // Make child widgets
    this.makeChildWidgets();
};

ConvertWidget.prototype.refresh = function(changedTiddlers) {
    var changedAttributes = this.computeAttributes();
    if(changedAttributes.to || changedAttributes["class"] || changedAttributes.selectedClass || changedAttributes.style) {
        this.refreshSelf();
        return true;
    }
    return this.refreshChildren(changedTiddlers);
};

exports.convert = ConvertWidget;

})();
",
            "title": "$:/plugins/gsd5/core/modules/widgets/convert.js",
            "type": "application/javascript",
            "module-type": "widget"
        },
        "$:/plugins/gsd5/core/modules/widgets/gsdtiddler.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/modules/widgets/gsdtiddler.js
type: application/javascript
module-type: widget

New GSD5 Tiddler Widget

\*/
(function() {

// jslint node: true, browser: true
// global $tw: false
"use_strict";
var Widget = require("$:/core/modules/widgets/widget.js").widget;
var GSDTiddler = function(parseTreeNode,options) {
    this.initialise(parseTreeNode, options);
};
// Inherit from Widget
GSDTiddler.prototype = new Widget();
// Render to DOM
GSDTiddler.prototype.render = function(parent,nextSibling) {
    var self = this;
    this.parentDomNode = parent;
    this.computeAttributes();
    this.execute();
    // Create DOM element
    var domNode = this.document.createElement("button");
    domNode.className = this["class"];
    if(this.title) {
        domNode.setAttribute("title", this.title);
    }
    if(this["aria-label"]) {
        domNode.setAttribute("aria-label", this["aria-label"]);
    }
    // Add event listener
    domNode.addEventListener(
        "click",
        function(event) {
        self.handleClick(event);
        },
        false
    );
    // Insert element
    parent.insertBefore(domNode, nextSibling);
    this.renderChildren(domNode, null);
    this.domNodes.push(domNode);
};

GSDTiddler.prototype.handleClick = function(event) {
    var title = prompt("Enter title:");
    if(!title) {
        return;
    }
    /*
    Special thanks to Stephan "Skeeve" Hradek for the NewTiddler plugin.
    Much of the following code is pulled from his plugin.
    http://tiddlystuff.tiddlyspot.com
    */
    var skeleton = this.wiki.getTiddlerAsJson(this.newtiddlerSkeleton);
    var skeletonClone = JSON.parse(this.substituteVariableReferences(skeleton));
    var basetitle = title;
    var newTitle = basetitle;
    for(var t=1; this.wiki.tiddlerExists(newTitle); t++) {
        newTitle = basetitle + " " + t;
    }
    skeletonClone.title = newTitle;
    var created = this.wiki.getCreationFields();
    for(var creationField in created) {
        skeletonClone[creationField] = created[creationField];
    }
    var modified = this.wiki.getModificationFields();
    for(var modificationField in modified) {
        skeletonClone[modificationField] = modified[modificationField];
    }
    this.wiki.addTiddler(skeletonClone);
    switch(this.newtiddlerEdit) {
        case "show":
        case "yes":
            var bounds = this.domNodes[0].getBoundingClientRect();
            this.dispatchEvent({
                type: "tm-navigate",
                navigateTo: newTitle,
                navigateFromTitle: this.getVariable("currentTiddler"),
                navigateFromNode: this,
                navigateFromClientRect: { top: bounds.top, left: bounds.left, width: bounds.width, right: bounds.right, bottom: bounds.bottom, height: bounds.height}
            });
            if(this.newtiddlerEdit === "yes") {
                this.dispatchEvent({type: "tm-edit-tiddler", tiddlerTitle: newTitle});
            }
            break;
        case "no":
            break;
    }
    var self = this;
    setTimeout(function() {
    // Set the dependant on-the-fly, if enabled.
    if(self.setNow==="true") {
		var gsdType = self.getVariable("gsd_type");
		// Set the target tiddler, usually currentTiddler, gsd_* field to the title of the newly created GSD5 tiddler.
		self.wiki.setText(self.parentTiddler, "gsd_"+gsdType, null, title);
	}
    $tw.rootWidget.dispatchEvent({type: "tm-auto-save-wiki"});
    }, 500);
};

GSDTiddler.prototype.execute = function() {
    // Get attributes
    this["class"] = this.getAttribute("class", "");
    this["aria-label"] = this.getAttribute("aria-label");
    this.title = this.getAttribute("title");
    this.newtiddlerSkeleton = this.getAttribute("skeleton");
    this.newtiddlerEdit = this.getAttribute("edit", "show");
    this.parentTiddler = this.getAttribute("tiddler", this.getVariable("currentTiddler"));
    this.setNow = this.getAttribute("setNow", "false");
    this.makeChildWidgets();
};

GSDTiddler.prototype.refresh = function(changedTiddlers) {
    var changedAttributes = this.computeAttributes();
    if(changedAttributes["class"] || changedAttributes.edit) {
        this.refreshSelf();
        return true;
    }
    return this.refreshChildren(changedTiddlers);
};

exports.gsdtiddler = GSDTiddler;

})();
",
            "title": "$:/plugins/gsd5/core/modules/widgets/gsdtiddler.js",
            "type": "application/javascript",
            "module-type": "widget"
        },
        "$:/plugins/gsd5/core/modules/widgets/prereq.js": {
            "text": "/*\
title: $:/plugins/gsd5/core/modules/widgets/prereq.js
type: application/javascript
module-type: widget

Widget to set prerequisite actions.

\*/
(function() {

// jslint node: true, browser: true
// global $tw: false
"use_strict";

var Widget = require("$:/core/modules/widgets/widget.js").widget;

var PrereqWidget = function(parseTreeNode,options) {
    this.initialise(parseTreeNode, options);
};

// Inherit from Widget
PrereqWidget.prototype = new Widget();

// Render to DOM
PrereqWidget.prototype.render = function(parent,nextSibling) {
    var self = this;
    this.parentDomNode = parent;
    this.computeAttributes();
    this.execute();
    this.selectDomNode = this.document.createElement("select");
    this.selectDomNode.addEventListener("change", function() {
        self.handleChangeEvent();
    });
    parent.insertBefore(this.selectDomNode,nextSibling);
    this.renderChildren(this.selectDomNode,null);
    this.domNodes.push(this.selectDomNode);
    this.setSelectValue();
};

PrereqWidget.prototype.execute = function() {
    this.prereqTitle = this.getAttribute("tiddler",this.getVariable("currentTiddler"));
    this.prereqDefault = this.getAttribute("default");
    this.makeChildWidgets();
};

PrereqWidget.prototype.setSelectValue = function() {
    var value = this.prereqDefault;
    var tiddler = this.wiki.getTiddler(this.prereqTitle);
    if(tiddler.fields.gsd_action) {
        value = tiddler.fields.gsd_action;
    }
    this.selectDomNode.value = value;
};

PrereqWidget.prototype.handleChangeEvent = function() {
    var tiddler = this.wiki.getTiddler(this.prereqTitle);
    if(tiddler.fields.gsd_type !== "action") {
        return;
    }
    this.wiki.setText(this.prereqTitle, "gsd_action", null, this.selectDomNode.value);
    this.setStatus();
};

PrereqWidget.prototype.setStatus = function() {
    if(this.selectDomNode.value !== "none") {
        var currentTiddler = this.wiki.getTiddler(this.prereqTitle);
        if(currentTiddler.fields.gsd_completed !== "completed" || currentTiddler.fields.gsd_status !== "waiting") {
            this.wiki.setText(this.prereqTitle, "gsd_status", null, "future");
        }
    }
};

PrereqWidget.prototype.refresh = function(changedTiddlers) {
    var changedAttributes = this.computeAttributes();
    if(changedAttributes.prereqTitle) {
        this.refreshSelf();
        return true;
    } else {
        if(changedTiddlers[this.prereqTitle]) {
            this.setSelectValue();
        }
        return this.refreshChildren(changedTiddlers);
    }
};

exports.prereq = PrereqWidget;

})();
",
            "title": "$:/plugins/gsd5/core/modules/widgets/prereq.js",
            "type": "application/javascript",
