                        var type = tiddler.fields.type || 'text/vnd.tiddlywiki';
                        if (!type.startsWith('text/')) {
                            continue;
                        }
                        if ('draft.of' in tiddler.fields) {
                            continue;
                        }
                        isIndexDirty = true;
                        updateTiddler(index, tiddler);
                    }
                }
                else { // change.deleted
                    isIndexDirty = true;
                    index.remove({ title: title });
                }
