    "tiddlers": {
        "$:/.tb/macros/calc": {
            "created": "20150126131341956",
            "creator": "Tobias Beer",
            "modified": "20150211234300814",
            "modifier": "inmysocks",
            "module-type": "macro",
            "summary": "allows simple calculations based on tiddler fields",
            "tags": "[[Icon Menus]]",
            "title": "$:/.tb/macros/calc",
            "type": "application/javascript",
            "text": "/*\
title: $:/.tb/macros/calc.js
type: application/javascript
module-type: macro

Computes a (Field) value +,-,*,/ a provided value.

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Information about this macro
*/

exports.name = "calc";

exports.params = [
	{name: "value"},
	{name: "operation"},
	{name: "until"},
	{name: "beyond"},
	{name: "decimals"},
	{name: "tiddler"}
];

/*
Run the macro
*/
exports.run = function(value, operation, until, beyond, decimals, tiddler) {
	if(!value) {
		return;
	}
	if("" == operation){
		operation = "0";
	}

	var 
		curr,dec,op,r,result,val,
		ops = ["+","-","*","/"];

	curr = parseFloat(
		0 > value.indexOf('!!') ?
		value :
		this.wiki.getTextReference(value, "NaN", tiddler || this.getVariable("currentTiddler"))
	);

	until = parseFloat(until);
	decimals = parseInt(decimals);

	op = operation.charAt(0);
	val = parseFloat(0 > ops.indexOf(op) ? operation : operation.substr(1));
	op = 0 > ops.indexOf(op) ? "+" : op;

	if(isNaN(curr)) {
		result = "NaN";
	} else {
		switch (op){
			case "-": result = curr - val; break;
			case "*": result = curr * val; break;
			case "/": result = curr / val; break;
			case "+":
			default: result = curr + val;		
		}
		if(!isNaN(until)) {
			if (!(
				"+" == op || "*" == op ?
				result <= until :
				result >= until
			)) {
				if("true" == beyond) {
					result = true;
				} else {
					result = until;
				}
			}
			if(beyond && result !== true) {
				result = false;
			}
		}
	}

	if(!isNaN(result)){
		r = result.toString();
		dec = r.indexOf('.');
		if(dec > -1){
			dec = r.substr(dec).length;
			if(
				!isNaN(decimals) && dec > decimals ||
				isNaN(decimals) && dec > 2
			){
				result = result.toFixed(isNaN(decimals) ? 2 : decimals);
			}			
		}
	}
	return result;
};

})();
"
