        "$:/plugins/rboue/Three.js/Lib/tools.js": {
            "type": "application/javascript",
            "title": "$:/plugins/rboue/Three.js/Lib/tools.js",
            "module-type": "library",
            "text": "// Tools for playing more easily with Three.js library.
//

(function() {

  var Tools = {};

  Tools.Stats = require("$:/plugins/rboue/Three.js/Lib/stats-min.js").Stats;
  Tools.dat = require("$:/plugins/rboue/Three.js/Lib/dat-gui-min.js").dat;

  // Resolve PATH
  Tools.resolvePath = function(path) {
    if (!tiddlyWiki) return null;
    return(tiddlyWiki.resolvePath(path));
  };

  // Debug tools
  //
  Tools.log = function() {
    this.flagActive = true;
  };
  Tools.log.prototype = {
    start: function() {this.flagActive = true;},
    stop: function() {this.flagActive = false;},
    log: function(mess, flagNoLog, fonc) {
      if (!this.flagActive || flagNoLog) return;
      console.log((fonc ? fonc+": " : "")+mess);
    },
    logPoint: function(x,y) {return "("+x+","+y+")";}
  };

  // Animation object
  //
  Tools.Animation = function(node, renderFunc, flagStartStopOnClick, flagLog) {
    var func = "Tools.Animation";
    this.flagNoLog = !flagLog;
    this.node = node; // Container DOM node
    this.renderFunc = renderFunc; // rendering
    this.idAnimation = null; // Internal id used by cancelAnimationFrame() to stop animation
    this.flagAnimation = true;

    this.nodeId = this.node.getAttribute("id");
    if (!this.nodeId)
      alert("Tools.Animation error: DOM node has no \"id\" attribute. Can't stop animation !");

    // Start/Stop on click
    if (flagStartStopOnClick) {
      var self = this;
      this.node.addEventListener("mousedown", function(event) {
        func = "Tools.Animation.onMouseDown";
        self.startStop();
      }, false);
    }
  };
  Tools.Animation.prototype = {
    start: function() {
      var func = "Tools.Animation.start";
      hack.log("", this.flagNoLog, func);
      this.flagAnimation = true;
      var self = this;
      var animate = function() {
        var func = "Tools.Animation.start.animate";
        // Cancel animation when tiddler has been closed (i.e. widget's DOM node disappears, cf.
        // Jeremy Ruston 14/12/07 in TiddlyWikiDev group).
        if (!document.getElementById(self.nodeId)) {
          hack.log("animation canceled: tiddler closes", self.flagNoLog, func);
          self.stop();
          return;
        }
        self.idAnimation = window.requestAnimationFrame(animate);
        self.renderFunc();
      };
      animate();
    },
    isClosed: function() {
      var func = "Tools.Animation.isClosed";
      // Cancel animation when tiddler has been closed (i.e. widget's DOM node disappears, cf.
      // Jeremy Ruston 14/12/07 in TiddlyWikiDev group).
      if (this.nodeId && ! document.getElementById(this.nodeId)) {
        hack.log("animation canceled: tiddler closes", this.flagNoLog, func);
        this.stop();
        return true;
      }
      return false;
    },
    stop: function() {
      var func = "Tools.Animation.stop";
      hack.log("", this.flagNoLog, func);
      window.cancelAnimationFrame(this.idAnimation);
      this.flagAnimation = false;
    },
    startStop: function() {
      if (this.flagAnimation)
        this.stop();
      else
        this.start();
    }
  };

  // Toolbar container for stats and menu
  //
  Tools.toolbarId = "toolbar"; // id of toolbar  
  Tools.toolbarGet = function(node) {
    var nodeId = node.getAttribute("id");
    if (!nodeId) return null;
    var toolbarId = nodeId + "-" + Tools.toolbarId;
    var toolbar = document.getElementById(toolbarId);
    if (!toolbar) {
      var toolbar = document.createElement("div");
      toolbar.setAttribute("id", toolbarId);
      toolbar.style.position = "relative";
      toolbar.style.top = "" + (-node.offsetHeight - 5) + "px";
      toolbar.style.zIndex = 10;
      node.appendChild(toolbar);
    }
    return toolbar;
  };

  // Statistic viewer at upper left corner of widget DOM <node>
  //
  Tools.addStats = function(node) {
    var stats = new Tools.Stats();
    stats.domElement.style.position = "relative";
    stats.domElement.style.display = "inline-block";
    stats.domElement.style.verticalAlign = "top";

    // Install stats in toolbar
    var toolbar = Tools.toolbarGet(node);
    if (toolbar.firstChild)
      toolbar.insertBefore(stats.domElement, toolbar.firstChild);
    else
      toolbar.appendChild(stats.domElement);
    return stats;
  };

  // GUI menu at upper right corner of widget DOM <node> 
  //
  Tools.Gui = function(node, flagOpen) {
    this.node = node;
    this.gui = new Tools.dat.GUI({autoPlace: false});
    this.gui.domElement.style.position = "relative";
    this.gui.domElement.style.display = "inline-block";
    this.gui.domElement.style.verticalAlign = "top";
    this.gui.domElement.style.zIndex = 20;
    //this.onTiddlerRefresh();
    if (flagOpen)
      this.gui.open();
    else
      this.gui.close();

    // Install menu in toolbar
    this.toolbar = Tools.toolbarGet(this.node);
    this.toolbar.appendChild(this.gui.domElement);
  };
  Tools.Gui.prototype = {
    get: function() {
      return this.gui;
    },
    onTiddlerRefresh: function() {
      var offset = this.toolbar.childNodes.length >= 2 ? this.toolbar.firstChild.offsetWidth : 0;
      var left = this.toolbar.offsetWidth - offset < 245 ? 0 : this.toolbar.offsetWidth - offset - 245;
      this.gui.domElement.style.left = "" + left + "px";
    }
  };

  // Export renderer's view to tiddler
  // Parameters :
  //   <renderer> : renderer.
  //   <tiddlerTitle> : title of the target tiddler with respect to the filesystem mode configuration.
  //   <type> : type of tiddler : "image/png" (default), "image/jpeg" or "image/svg+xml".
  //   <flagOverwrite> : if true, overwrite existing tiddler without asking.
  // Saves renderer's wiew as a tiddler with required type.
  //
  Tools.exportView = function(renderer, tiddlerTitle, type, flagOverwrite) {
    var func = "Tools.exportView";
    if (!renderer.domElement) return;
    var tag = renderer.domElement.tagName.toUpperCase();
    if ((((type == "image/png") || (type == "image/jpeg")) && (tag != "CANVAS")) ||
        ((type == "image/svg+xml") && (tag != "SVG"))) {
      console.error(func+": bad tiddler type : "+type)
      return;
    }
    TiddlyWiki.exportToTiddler(renderer.domElement, "tw:"+tiddlerTitle, type, flagOverwrite);
  };

  // Execute code when a condition is fulfilled
  // Parameters :
  //   <funcTest> : returns true when the condition is fulfilled, false otherwise.
  //   <funcExec> : code to execute.
  //   <periodicity> : periodicity of the active waiting mechanism (250 ms by default).
  //
  Tools.wait = function(funcTest, funcExec, periodicity) {
    var func = "Tools.wait", flagNoLog = true;
    var period = 250; // periodicity in ms
    if (periodicity) period = periodicity;
    var timerId = null;
    var doWait = function() {
      var func = "Tools.wait/doWait";
      if (!funcTest()) return;

      // Test OK
      hack.log("test OK !", flagNoLog, func);
      clearInterval(timerId);
      funcExec();
    };
    timerId = setInterval(doWait, period);
  };

  exports.Tools = Tools;
})();"
