      case AWD_FIELD_INT16:
        elem_len = 2;
        read_func = this.readI16;
        break;
      case AWD_FIELD_INT32:
        elem_len = 4;
        read_func = this.readI32;
        break;
      case AWD_FIELD_BOOL:
      case AWD_FIELD_UINT8:
        elem_len = 1;
        read_func = this.readU8;
        break;
      case AWD_FIELD_UINT16:
        elem_len = 2;
        read_func = this.readU16;
        break;
      case AWD_FIELD_UINT32:
      case AWD_FIELD_BADDR:
        elem_len = 4;
        read_func = this.readU32;
        break;
      case AWD_FIELD_FLOAT32:
        elem_len = 4;
        read_func = this.readF32;
        break;
      case AWD_FIELD_FLOAT64:
        elem_len = 8;
        read_func = this.readF64;
        break;
      case AWD_FIELD_VECTOR2x1:
      case AWD_FIELD_VECTOR3x1:
      case AWD_FIELD_VECTOR4x1:
      case AWD_FIELD_MTX3x2:
      case AWD_FIELD_MTX3x3:
      case AWD_FIELD_MTX4x3:
      case AWD_FIELD_MTX4x4:
        elem_len = 8;
        read_func = this.readF64;
        break;
    }

    if (elem_len < len) {
      var list;
      var num_read;
      var num_elems;

      list = [];
      num_read = 0;
      num_elems = len / elem_len;

      while (num_read < num_elems) {
        list.push(read_func.call( this ) );
        num_read++;
      }

      return list;
    }
    else {
      return read_func.call( this );
    }

  }


