            "text": "/**
 * Loader for CTM encoded models generated by OpenCTM tools:
 *	http://openctm.sourceforge.net/
 *
 * Uses js-openctm library by Juan Mellado
 *	http://code.google.com/p/js-openctm/
 *
 * @author alteredq / http://alteredqualia.com/
 */

THREE.CTMLoader = function ( showStatus ) {

	THREE.Loader.call( this, showStatus );

};

THREE.CTMLoader.prototype = Object.create( THREE.Loader.prototype );

// Load multiple CTM parts defined in JSON

THREE.CTMLoader.prototype.loadParts = function( url, callback, parameters ) {
  var func = "THREE.CTMLoader.loadParts";

	parameters = parameters || {};

	var scope = this;

  if (TiddlyWiki && url.match(/^tw:/)) {
    var res = TiddlyWiki.load(url);
    //hack.log("res.length = "+res.length, hack.loader, func);

    var basePath = parameters.basePath ? parameters.basePath : this.extractUrlBase(url);
    var jsonObject = JSON.parse(res);
    var materials = [], geometries = [], counter = 0;
    function callbackFinal(geometry) {
      counter += 1;
      geometries.push(geometry);
      if (counter === jsonObject.offsets.length) {
	callback(geometries, materials);
      }
    }

    // init materials
    for (var i = 0; i < jsonObject.materials.length; i++) {
      materials[i] = scope.createMaterial(jsonObject.materials[i], basePath);
    }

    // load joined CTM file
    var partUrl = basePath + jsonObject.data;
    var parametersPart = {useWorker: parameters.useWorker, offsets: jsonObject.offsets};
    scope.load(partUrl, callbackFinal, parametersPart);

    hack.log("url: "+url+": loaded", hack.loader, func);
    return;
  }

	var xhr = new XMLHttpRequest();

	var basePath = parameters.basePath ? parameters.basePath : this.extractUrlBase( url );

	xhr.onreadystatechange = function() {

		if ( xhr.readyState === 4 ) {

			if ( xhr.status === 200 || xhr.status === 0 ) {

				var jsonObject = JSON.parse( xhr.responseText );

				var materials = [], geometries = [], counter = 0;

				function callbackFinal( geometry ) {

					counter += 1;

					geometries.push( geometry );

					if ( counter === jsonObject.offsets.length ) {

						callback( geometries, materials );

					}

				}


				// init materials

				for ( var i = 0; i < jsonObject.materials.length; i ++ ) {

					materials[ i ] = scope.createMaterial( jsonObject.materials[ i ], basePath );

				}

				// load joined CTM file

				var partUrl = basePath + jsonObject.data;
				var parametersPart = { useWorker: parameters.useWorker, offsets: jsonObject.offsets };
				scope.load( partUrl, callbackFinal, parametersPart );

			}

		}

	}

	xhr.open( "GET", url, true );
	xhr.setRequestHeader( "Content-Type", "text/plain" );
	xhr.send( null );

};

// Load CTMLoader compressed models
//	- parameters
//		- url (required)
//		- callback (required)

THREE.CTMLoader.prototype.load = function( url, callback, parameters ) {
  var func = "THREE.CTMLoader.load";

	parameters = parameters || {};

	var scope = this;

	var offsets = parameters.offsets !== undefined ? parameters.offsets : [ 0 ];

  if (TiddlyWiki && url.match(/^tw:/)) {
    var res = TiddlyWiki.load(url);
    //hack.log("res.length = "+res.length, hack.loader, func);

    var binaryData = new Uint8Array(res);
    var s = Date.now();
    if (parameters.useWorker) {
      if (TiddlyWiki) {
        alert(func+": Worker() not supported !");
        return;
      }
      var worker = new Worker("js/loaders/ctm/CTMWorker.js");
      worker.onmessage = function(event) {
	var files = event.data;
	for (var i = 0; i < files.length; i++) {
	  var ctmFile = files[ i ];
	  var e1 = Date.now();
	  // console.log( "CTM data parse time [worker]: " + (e1-s) + " ms" );
	  scope.createModel( ctmFile, callback );
	  var e = Date.now();
	  console.log("model load time [worker]: " + (e-e1) + " ms, total: " + (e-s));
	}
      };
      worker.postMessage({"data": binaryData, "offsets": offsets});
    } else {
      for (var i = 0; i < offsets.length; i++) {
	var stream = new CTM.Stream(binaryData);
	stream.offset = offsets[i];
	var ctmFile = new CTM.File(stream);
	scope.createModel(ctmFile, callback);
      }
      //var e = Date.now();
      //console.log( "CTM data parse time [inline]: " + (e-s) + " ms" );
    }

    hack.log("url: "+url+": loaded", hack.loader, func);
    return;
  }

	var xhr = new XMLHttpRequest(),
		callbackProgress = null;

	var length = 0;

	xhr.onreadystatechange = function() {

		if ( xhr.readyState === 4 ) {

			if ( xhr.status === 200 || xhr.status === 0 ) {

				var binaryData = new Uint8Array(xhr.response);

				var s = Date.now();

				if ( parameters.useWorker ) {

					var worker = new Worker( "js/loaders/ctm/CTMWorker.js" );

					worker.onmessage = function( event ) {

						var files = event.data;

						for ( var i = 0; i < files.length; i ++ ) {

							var ctmFile = files[ i ];

							var e1 = Date.now();
							// console.log( "CTM data parse time [worker]: " + (e1-s) + " ms" );

							scope.createModel( ctmFile, callback );

							var e = Date.now();
							console.log( "model load time [worker]: " + (e-e1) + " ms, total: " + (e-s));

						}


					};

					worker.postMessage( { "data": binaryData, "offsets": offsets } );

				} else {

					for ( var i = 0; i < offsets.length; i ++ ) {

						var stream = new CTM.Stream( binaryData );
						stream.offset = offsets[ i ];

						var ctmFile = new CTM.File( stream );

						scope.createModel( ctmFile, callback );

					}

					//var e = Date.now();
					//console.log( "CTM data parse time [inline]: " + (e-s) + " ms" );

				}

			} else {

				console.error( "Couldn't load [" + url + "] [" + xhr.status + "]" );

