        "$:/plugins/rboue/Three.js/tiddlywiki.js": {
            "text": "/*\
title: $:/plugins/rboue/Three.js/tiddlywiki.js
type: application/javascript
module-type: library

  Adaptation of Three.js to TiddlyWiki.

\*/
(function() {

  var TiddlyWiki = function(widget, tiddlerTitle, widgetConfiguration) {
    this.widget = widget; // widget
    this.tiddlerTitle = tiddlerTitle;   // title of the main code tiddler
    this.widgetConfiguration = widgetConfiguration; // widget's configuration object
  };

  // Static members

  // Initialized at TiddlyWiki session opening
  TiddlyWiki.sessionConfiguration = null; // Session configuration

  // Static initialisations
  TiddlyWiki.init = function(sessionConfiguration) {
    TiddlyWiki.sessionConfiguration = sessionConfiguration;
  };

  // Get CSS raw source of a tiddler image
  TiddlyWiki.getRawTiddlerImage = function(tiddlerName) {
    if (!$tw || !$tw.wiki) return false;
    // Check if it is an image tiddler
    if (!$tw.wiki.isImageTiddler(tiddlerName)) return false;
    var tiddler = $tw.wiki.getTiddler(tiddlerName);
    if ((typeof(tiddler) == "undefined") || !tiddler) return false;
    var text = tiddler.fields.text;
    if (!text) return false;
    var type = tiddler.fields.type;
    // Render the appropriate element for the image type
    var src = "";
    switch(type) {
    case "image/svg+xml":
      src = "data:image/svg+xml," + encodeURIComponent(text);
      break;
    default:
      src = "data:" + type + ";base64," + text;
      break;
    }

    return src;
  }; 

  // Loading of TiddlyWiki specific URL
  // Parameters :
  //   <url> : URL with specific syntax : "tw:<tiddler title>"
  //   <type> : "image" for image tiddler, "ArrayBuffer" for tiddler with base64 content.
  // Returns the tiddler data (text or binary) or null.
  // The type of data (text or binary) is deduced from the tiddler's type.
  // Type image/xx means tiddler image.
  // Type application/octet-stream means binary data coded with base64. In that case an ArrayBuffer
  // is returned.
  // Other types means plain text.
  TiddlyWiki.load = function(url) {
    var func = "TiddlyWiki.load";
    if (!url || !url.match(/^tw:/)) return null;
    hack.log("url: "+url, hack.loader, func);
    var tiddlerTitle = tiddlyWiki.resolvePath(url.replace("tw:", ""), true);
    var tiddler = $tw.wiki.getTiddler(tiddlerTitle);
    if ((typeof(tiddler) == "undefined") || !tiddler) {
      throw new Error("TiddlyWiki.load: \"" + url + "\" : unknown tiddler.");
    }
    if (tiddler.fields.type.match(/^image\//))
      return TiddlyWiki.getRawTiddlerImage(tiddlerTitle);
    else {
      if (!tiddler.fields.text) {
        console.error("TiddlyWiki.load: \"" + url + "\" seems to be unreachable or the tiddler is empty.");
        return null;
      }
      if (tiddler.fields.type == "application/octet-stream")
        return StringView.makeFromBase64(tiddler.fields.text).buffer;
      else
        return tiddler.fields.text;
    }
  };

  // Export current view to an image tiddler
  // Parameters :
  //   <domElement> : DOM element, <canvas> or <svg>.
  //   <url> : URL with specific syntax : "tw:<tiddler title>".
  //   <type> : type of image tiddler : "image/png" (default) or "image/jpeg".
  //   <flagOverwrite> : if true, overwrites existing tiddler without asking.
  // Saves view data as a tiddler image with required type.
  TiddlyWiki.exportToTiddler = function(domElement, url, type, flagOverwrite) {
    var func = "TiddlyWiki.exportToTiddler";
    if (!url || !url.match(/^tw:/)) {
      console.error(func+": bad URL format, not a tiddler.");
      return;
    }
    hack.log("url: "+url, hack.render, func);
    var tag = domElement.tagName.toUpperCase();
    if (!type) {
      if (tag == "SVG")
        type = "image/svg+xml";
      else
        type = "image/png";
    }
    if ((((type == "image/png") || (type == "image/jpeg")) && (tag != "CANVAS")) ||
        ((type == "image/svg+xml") && (tag != "SVG"))) {
      console.error(func+": bad tiddler type : "+type)
      return;
    }
    var tiddlerTitle = tiddlyWiki.resolvePath(url.replace("tw:", ""), true);
    if (!flagOverwrite) {
      var tiddler = $tw.wiki.getTiddler(tiddlerTitle);
      if ((typeof(tiddler) != "undefined") && tiddler) {
        if (!confirm("View image saving : tiddler <"+tiddlerTitle+"> exists, would you like to overwrite it ?")) return;
      }
    }
    var data = null;
    if (tag == "SVG") {
      data = domElement.outerHTML;
      type = ""; // bad TiddlyWiki behavior (with 5.1.5) !?
    } else
      data = domElement.toDataURL(type).replace(/^data:.*?;base64,/, "");
    //console.log(func+": data = "+data);

    // Create the image tiddler
    $tw.wiki.addTiddler(new $tw.Tiddler({title: tiddlerTitle, type: type, text: data}));
  };

  // Create a Worker to execute inline code
  TiddlyWiki.createWorker = function (func) {
    var blobURL = URL.createObjectURL(new Blob(
      ["(", func.toString(), ")()"],
      {type: "application/javascript"}
    ));
    var worker = new Worker(blobURL);
    URL.revokeObjectURL(blobURL);

    return worker;
  };


  // Instance members

  TiddlyWiki.prototype = {

    // Resolve file path.
    //
    // Following behaviour apply only if widget's configuration's object property "filesystem"
    // is defined. If not, file is assumed to be a tiddler's title.
    //
    // File path resolution is as close as possible to Unix (for executables, libraries,
    // manuals, ...).
    // We make assumption that tiddler title follows classical Unix Path pattern
    // like : /dir1/dir2/ ... /dir/file. If not the case, file should be
    // in the "current" directory, or completely out of path convention.
    // The curent directory (if any) is the "directory" of the current sheet tiddler.
    // Example :
    //   File to resolve = myTiddler
    //   Tiddler title of the current sheet = MyProject/MySubProject/calc1
    //   Current directory = MyProject/MySubProject
    //   Sheet file in filesystem mode = calc1
    //   File path = MyProject/MySubProject/myTiddler
    //
    // Unlike Unix, there is no notion of root directory or "/". TiddlyWiki has no
    // real filesystem so we can't rely on a root node. Relative versus absolute
    // path is deduced from the following. If the first caracter of a file
    // path is "/" and there is no more "/" after that, then the current directory is "".
    // Remark that the behavior is identical to Unix. A file path beginning with a "/"
    // will be directly converted in a tiddler title.
    // By default (and unlike Unix), malformed path like multiple "/" instead of one
    // are not accepted. For example "//" is not reduced to "/" and treated like a
    // path separator. It is the responsibility of the user to be clear about the significance of
    // it's file reference, path-like name or tiddler title. For same reason,
    // blank characters, eventually around "/", are treated like ordinary characters
    // and not deleted in the final tiddler title.
    // With optionnal <flagNormalize> parameter set, malformed path are normalized like
    // Unix does, but blank characters are still not deleted.
    //
    // If not found in the current directory, a file is searched under a collection
    // of path directories, pre-defined in the PATH variable. Those directories are
    // absolute (i.e. not relative to current directory).
    // Syntax of the PATH variable :
    //   <path1>';'<path2>';' ... ';'<pathN>
    // The PATH is searched first in a variable defined with a <$set> widget (not actually
    // implemented, maybe in the future if someone asks :), then in the "path" parameter
    // of the <$edit-socialcalc> widget, then in the widget's configuration object, under
    // filesystem.PATH property. The widget's configuration object is searched first in
    // $:/config/Three.js tiddler of "application/json" type, then in
    // $:/plugins/Three.js/config-defaults.json tiddler.
    // Please note that final PATH value is never a combination of different sources. The first
    // source that defines a valid PATH (either <$set> variable, "path" parameter of
    // <$edit-socialcalc> widget or widget's configuration object) gives the proper PATH value
    // and stops all subsequent PATH search.
    //
    // Search algorithm :
    //   <file> : input file path.
    //   <tiddlerTitle> : output.
    //   <currentTiddlerTitle> : tiddler title of the current sheet.
    //   PATH <- value of a "path" variable defined with <$set> widget, or "path" field
    //           of <currentTiddlerTitle>.
    //   currentDir <- path of <currentTiddlerTitle> (anything before the last '/', or "").
    //   if <file> begins with "./" or "../" then
    //     # Relative path
    //     <finalPath> <- ... (search à la Unix)
    //     if tiddler <finalPath> found then return <finalPath>
    //   else
    //     # Under <currentDir> or under a PATH directory, or absolute
    //     if tiddler <currentDir>/<file> found then return <currentDir>/<file>
    //     if <PATH> exists then
    //       for all <dir> in <PATH>
    //         if tiddler <dir>/<file> found then return <dir>/<file>
    //       end for
    //     end if
    //     if tiddler <file> found then return file
    //   end if
    //
    resolvePath: function(file, flagNormalize) {
      var fonc = "TiddlyWiki.resolvePath";

      hack.log("============= file = "+file, hack.filesystem, fonc);

      if (!file) return null;
      var tiddlerTitle = "";
      var tiddler = false;
      if (!this.widgetConfiguration || !this.widgetConfiguration.filesystem) {
        hack.log("no filesystem in widget's configuration !", hack.filesystem, fonc);
        return(file);
      }

      // Initialisation
      var currentTiddlerTitle = this.tiddlerTitle
      var currentDir = "";
      tiddler = $tw.wiki.getTiddler(currentTiddlerTitle);
      if ((typeof(tiddler) == "undefined") || !tiddler) return null;

      // In case current tiddler is edited, we refer to the "file", not the draft tiddler
      if (tiddler.isDraft()) currentTiddlerTitle = tiddler.fields["draft.of"];
      hack.log("currentTiddlerTitle = "+currentTiddlerTitle, hack.filesystem, fonc);

      // Get PATH
      var tabPath = null;
      if (this.widgetConfiguration.filesystem.PATH && this.widgetConfiguration.filesystem.PATH != "")
        tabPath = this.widgetConfiguration.filesystem.PATH.split(/[\s]*;[\s]*/);
      hack.log("tabPath = "+(tabPath ? tabPath.join(" | "): tabPath), hack.filesystem, fonc);

      currentDir = currentTiddlerTitle.replace(/[\/][^\/]*$/, "").replace(/[\/][.][\/]/, "/");;
      hack.log("currentDir = "+currentDir, hack.filesystem, fonc);

      // File normalization
      if (flagNormalize) {
        file = file.replace(/[\/][\/]+/g, "/").replace(/\/\.\//g, "/");
        hack.log("file normalized = "+file, hack.filesystem, fonc);
      }

      if (file.match(/^\.\/|^\.\.\/|\/\.\.\//)) {
        // Relative path or with relative sub-expressions

        hack.log("relative path", hack.filesystem, fonc);
        tabCurrentDir = currentDir.split("/");
        hack.log("tabCurrentDir = "+tabCurrentDir.join(" | "), hack.filesystem, fonc);
        tabPageName = file.split("/");
        var tabTiddlerTitle = [];
        for (var i = 0; i < tabCurrentDir.length; i++)
          tabTiddlerTitle.push(tabCurrentDir[i]);
        hack.log("tabTiddlerTitle = "+tabTiddlerTitle.join(" | "), hack.filesystem, fonc);
        var indexInCurrentDir = tabCurrentDir.length - 1;
        hack.log("indexInCurrentDir = "+indexInCurrentDir, hack.filesystem, fonc);
        var flagAbsolute = false;
        for (var i = 0; i < tabPageName.length; i++) {
          hack.log("boucle: tabPageName["+i+"] = "+tabPageName[i], hack.filesystem, fonc);
          hack.log("  indexInCurrentDir = "+indexInCurrentDir, hack.filesystem, fonc);
          hack.log("  tabTiddlerTitle = "+tabTiddlerTitle.join(" | "), hack.filesystem, fonc);
          if (tabPageName[i] == ".") continue;
          if (tabPageName[i] == "..") {
            indexInCurrentDir--;
            if (tabTiddlerTitle.length == 0) {
              // Malformed path -> it seems to be an absolute non-path tiddler title
              hack.log("  malformed path case 1", hack.filesystem, fonc);
              flagAbsolute = true;
              break;
            }
            tabTiddlerTitle.pop();
            continue;
          }
          indexInCurrentDir++; // alas ! maybe we will return above currentDir
          hack.log("  tabCurrentDir[indexInCurrentDir] = "+tabCurrentDir[indexInCurrentDir], hack.filesystem, fonc);
          if (indexInCurrentDir < tabCurrentDir.length - 1) {
            // We wander above currentDir
            if ((indexInCurrentDir < 0) || (tabPageName[i] != tabCurrentDir[indexInCurrentDir])) {
              // Malformed path -> it seems to be an absolute non-path tiddler title
              hack.log("  malformed path case 2", hack.filesystem, fonc);
              flagAbsolute = true;
              break;
            }
          }
          tabTiddlerTitle.push(tabPageName[i])
        }

        if (!flagAbsolute) {
          tiddlerTitle = tabTiddlerTitle.join("/");
        } else {
          tiddlerTitle = file;
        }

      } else {
        // Not relative path
        hack.log("absolute path (under curent dir, PATH dirs or really absolute)", hack.filesystem, fonc);
        var flagFind = false;

        // Search under currentDir
        tiddlerTitle = currentDir + "/" + file;
        var tiddler1 = $tw.wiki.getTiddler(tiddlerTitle);
        if ((typeof(tiddler1) != "undefined") && tiddler1)
          flagFind = true;

        if (!flagFind && tabPath) {
          // Search under PATH directories
          for (var i = 0; i < tabPath.length; i++) {
            tiddlerTitle = tabPath[i] + "/" + file;
            var tiddler2 = $tw.wiki.getTiddler(tiddlerTitle);
            if ((typeof(tiddler2) != "undefined") && tiddler2) {
              flagFind = true;
              break;
            }
          }
        }

        // Maybe a tiddler already
        if (!flagFind)
          tiddlerTitle = file;
      }

      hack.log("============= tiddler = "+tiddlerTitle, hack.filesystem, fonc);
      return tiddlerTitle;
    }
  };

  exports.TiddlyWiki = TiddlyWiki;

})();",
