        "$:/core/modules/editor-operations/text/balancecheck.js": {
            "text": "/*\
title: $:/core/modules/editor-operations/text/replace-selection.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to replace the selection

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["balancecheck"] = function(event,operation) {
var listtext=operation.text;
	operation.replacement="";
	operation.cutStart = operation.selStart;
	operation.cutEnd = operation.selEnd;
	operation.newSelStart = operation.selStart;
	operation.newSelEnd = operation.selStart + operation.replacement.length;

    var s=listtext;
    var parentheses = "[]{}()",
    others ="~~//__''^^,,@@``", //other matched pairs
    stack = [], //Parentheses stack
    dualstack=[], //Duals stack
    locstack = [], //position of found
    locdualstack=[], // position of found duals
    i, //Index in the string
    dual, //two characters
    c; //Character in the string

  for (i = 0; c = s[i++];)
  {
dual = c+s[i];
var dualposition = others.indexOf(dual);
if (dualposition>=0){

// it is on the others list
if (dualstack.length==0){
// The stack is empty so add it
dualstack.push(dual);
locdualstack.push(i);
} else {
//The stack is not empty. What is on the stack?

if (dual==dualstack[dualstack.length-1]) {dualstack.pop();
locdualstack.pop();
} else {
dualstack.push(dual);
locdualstack.push(i);
}
//alert(dualstack.length);
};

}

    var bracePosition = parentheses.indexOf(c), braceType;
    //~ is truthy for any number but -1
    if (!~bracePosition) 
        continue;

    braceType = bracePosition % 2 ? 'closed' : 'open';

    if (braceType === 'closed')
    {
      //If there is no open parenthese at all, return false OR
      //if the opening parenthese does not match ( they should be neighbours )
// stop immediately if we have a mismatch

if(!stack.length) {

 alert("close "+c+" without an open at "+i);
//this.iframeTextArea.setSelectionRange(i-1,i);
	operation.newSelStart = i-1;
	operation.newSelEnd = i;

return
}
// something in the stack. Get it and the location
var d= stack.pop();
var e =locstack.pop();

      if (parentheses.indexOf(d) != bracePosition - 1){

//wasn't a match so report and stop
       alert(d+" at position "+e+" does not match "+c+" at position "+i);
//this.iframeTextArea.setSelectionRange(e,i-1);

	operation.newSelStart = e;
	operation.newSelEnd = i-1;

        return;

}
    }
    else
    {
      stack.push(c);
      locstack.push(i);
    }
  }

//we've finished scanning through

//If anything is left on the stack <- not balanced

if(!stack.length && !dualstack.length) {
// nothing on either stack so we're good
alert("All balanced");}
else {
//something was left in one stack or the other
var d= stack.pop();
var e =locstack.pop();

if (e=== undefined){
//It was something on the dual stack

var d=dualstack.pop();
var e=locdualstack.pop();
//If this isn't the first entry then get the first one
if (!dualstack.length) {
//alert("nothing in the stack");
alert("Open "+d+" at location "+e);
	operation.newSelStart = e-1;
	operation.newSelEnd = e+1;

}
else
{
var b=dualstack[0];
var c= locdualstack[0];
       alert(d+" at position "+e+" does not match "+b+" at position "+c);
	operation.newSelStart = c+1;
	operation.newSelEnd = e-1;

}

} else {
//something on the single stack so report the location

alert("Open "+d+" at location "+e);
	operation.newSelStart = e-1;
	operation.newSelEnd = e;

}
}
return;


};

})();
