title: $:/core/modules/editor-operations/text/findreplace.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to replace the selection

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["findreplace"] = function(event,operation) {
var listtext=operation.text;
//dummy lastfound to allow testing of find and replace
var lastfound=0;
lastfound=this.wiki.getTextReference('$:/sk/lastfound');

var find = event.paramObject.find;
var replace = event.paramObject.replace;

if (find == "|") {find = "\|";}
if (find == "^t") {find = String.fromCharCode(9);}
if (find == "^f") {find = String.fromCharCode(10);}
if (find == "^r") {find = String.fromCharCode(13);}
if (replace == "|") {replace = "\|";}
if (replace == "^t") {replace = String.fromCharCode(9);}
if (replace == "^f") {replace = String.fromCharCode(10);}
if (replace == "^r") {replace = String.fromCharCode(13);}

var str=listtext;
    var lstr = str.length;
    if (lstr<lastfound) {lastfound = 0;
this.wiki.setText('$:/sk/lastfound','','',lastfound);
}

var n = listtext.indexOf(find,lastfound);
var len=find.length;

if (n<0) {
    lastfound=0;
this.wiki.setText('$:/sk/lastfound','','',lastfound);
operation.replacement=str;
	operation.cutStart = 0;
	operation.cutEnd = str.length;
}
else {

	operation.replacement=replace;
	operation.cutStart = n;
	operation.cutEnd = n+len;
	operation.newSelStart = n;
	operation.newSelEnd = n + operation.replacement.length;
    lastfound=n+len;
this.wiki.setText('$:/sk/lastfound','','',lastfound);
}
};

})();",
