title: $:/core/modules/editor-operations/text/replaceall.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to replaceall the selection

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["replaceall"] = function(event,operation) {
var listtext=operation.text;
var find = event.paramObject.find;
var replace = event.paramObject.replace;
var rwarn=false;
var r=true;

if (find == "|") {find = "\|";}
if (find == "^t") {find = String.fromCharCode(9);}
if (find == "^f") {find = String.fromCharCode(10);}
if (find == "^r") {find = String.fromCharCode(13);}
if (replace == "|") {replace = "\|";}
if (replace == "^t") {replace = String.fromCharCode(9);}
if (replace == "^f") {replace = String.fromCharCode(10);}
if (replace == "^r") {replace = String.fromCharCode(13);}

var regexfind = new RegExp(find, "gm");
var res = listtext.replace(regexfind,replace);

   if (rwarn == false) {
      r = confirm("Replace all can lead to unexpected results.\n                    Proceed anyway?"); 
      rwarn = true;
    }
    if (r == true) {

	operation.replacement=res;
	operation.cutStart = 0;
	operation.cutEnd = res.length;}
};

})();
",
