        "$:/core/modules/editor-operations/text/tablehelper.js": {
            "text": "/*\
title: $:/core/modules/editor-operations/text/replace-selection.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to replace the selection

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["tablehelper"] = function(event,operation) {
var listtext=operation.selection;
var target ="$:/config/WikiParserRules/Inline/commaconvert";
var commasr = $tw.wiki.getTiddlerText(target);

var target ="$:/config/WikiParserRules/Inline/centertable";
var centeroption = $tw.wiki.getTiddlerText(target);

var leadpipe="|";
var midpipe="|";
var finalpipe="|";

if(centeroption=="enable") {
var leadpipe="| ";
var midpipe=" | ";
var finalpipe=" |";
}

// strip tabs at the end of a line
  listtext= listtext.replace(/\t$/gm,'');

// convert tab to a pipe
  listtext=listtext.replace(/\t/gm,midpipe);


// convert start of line to pipe
  listtext=listtext.replace(/^/gm,leadpipe);

    if (commasr == "enable") {
  listtext=listtext.replace(/,/gm,midpipe);
    } else {
    }

// convert new line to pipe newline
  listtext=listtext.replace(/\n/gm,finalpipe+'\n');
// convert quote to space
  listtext=listtext.replace(/"/gm,'');
// convert stray last pipe
  listtext=listtext.replace(/\|\n\|$/gm,'|\n');
//make last character a pipe
  listtext=listtext.replace(/\b$/gm,'|');
//count the lines

var rowcount = (listtext.match(/\n/g) || []).length;

//Array rows contains the individual rows
var rows=listtext.split(/\n/)

//count the vertical pipes in each row

var maxcols=0;
var i=0;
var templist="";

while (i < rowcount) {
var colcount=0;
colcount = (rows[i].match(/\|/g) || []).length;
if (colcount > maxcols) {maxcols=colcount;}
i++;
}
//alert("MaxCols = "+maxcols);

var i=0;
while (i <= rowcount) {
colcount = (rows[i].match(/\|/g) || []).length;

while (colcount<maxcols) {
rows[i]=rows[i]+"|";
colcount = (rows[i].match(/\|/g) || []).length;
//alert(i+": "+rows[i]+" colcount = "+colcount);
}
var templist=templist + rows[i]+"\n";
//alert(templist);
i++;
} // end of second pass through the rows
//make first line a header
  listtext=templist.replace(/\|$/m,'|h');
operation.replacement=listtext;

	operation.cutStart = operation.selStart;
	operation.cutEnd = operation.selEnd;
	operation.newSelStart = operation.selStart;
	operation.newSelEnd = operation.selStart + operation.replacement.length;
};

})();
",
            "created": "20160327155515041",
            "modified": "20160505184929861",
            "module-type": "texteditoroperation",
            "title": "$:/core/modules/editor-operations/text/tablehelper.js",
            "type": "application/javascript"
        }
    }
}
