        "$:/core/modules/editor-operations/text/tablecols.js": {
            "text": "/*\
title: $:/core/modules/editor-operations/text/replace-selection.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to replace the selection

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["tablecols"] = function(event,operation) {
var listtext=operation.selection;
var changecol = event.paramObject.changecol;
var changetype = event.paramObject.changetype;
var replace="";
var templist =listtext;
    var re = /(.*?\n)(.*?\n)+/; 
    var subst = '$1'; 

    var row = templist.replace(re, subst);
    var count= 0, i, j;
    while((i= row.indexOf("|", i))!= -1){
        ++count;
        ++i;
    }
count=count-1;

//split the string into an array of rows

var rows=templist.split(/\n/)

var numrows=rows.length;
var temprow="";
//for each row

i=0;
while (i<numrows){

//if not a table bail out

//  get row
temprow = rows[i];

// split the row into array cell[cols]
var cells=temprow.split(/\|/)
var numcols =cells.length;
//if changecol>numcols then skip

if(numcols>changecol){

//cell[changecol].trim to eliminate excess
cells[changecol]=cells[changecol].trim();

// if special cell such  >,< or ~ then skip to put it back together
//
var specialcheck="\>\<\~hcfk".indexOf(cells[changecol]);

if (specialcheck < 0){
// select case of changetype
switch(changetype) {
    case "left":
        cells[changecol]=cells[changecol];
        break;
case "right":
        cells[changecol]=" "+cells[changecol];
        break;
case "center":
        cells[changecol]=" "+cells[changecol]+" ";
        break;
case "insert":
        cells[changecol]="|"+cells[changecol];
        break;
case "delete":
        cells[changecol]="";
}
//end specialcheck
} 
}
j=1;
temprow="";

while (j<numcols){
if (changetype != "delete" || j != changecol ) {
temprow=temprow+"|"+cells[j];}
++j;
}

// if special ending h,c,k or f then don't add

var specialcheck=cells[numcols-1].indexOf("chfk");
if (specialcheck>=0){
temprow=temprow+"|";
}
if (temprow.length>0){
replace=replace+temprow+"\n";}
//alert(replace);
//end of row loop
++i;
}

operation.replacement=replace;
	operation.cutStart = operation.selStart;
	operation.cutEnd = operation.selEnd;
	operation.newSelStart = operation.selStart;
	operation.newSelEnd = operation.selStart + operation.replacement.length;
};

})();
",
