        "$:/core/modules/editor-operations/text/tabletotiddler.js": {
            "text": "/*\
title: $:/core/modules/editor-operations/text/tabletotiddler.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to convert a table to a group of tiddlers

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports["tabletotiddler"] = function(event,operation) {
var listtext=operation.selection;
var replace="";
var temptext="";
var tagtext="";
var fieldtext="";
var templist =listtext;

    var re = /(.*?\n)(.*?\n)+/; 
    var subst = '$1'; 
    var row = templist.replace(re, subst);
    var count= 0, i, j;
    while((i= row.indexOf("|", i))!= -1){
        ++count;
        ++i;
    }
count=count-1;

//split the string into an array of rows

var rows=templist.split(/\n/);
var numrows=rows.length;
var temprow="";

//Process first two rows
//row[0] will be the descriptors with the first cell always assumed to be the title for the tiddler
//row[1] will designate the type of entry, principally tag, field or a single text entry

temprow = rows[0];
var types=temprow.split(/\|/);

temprow = rows[1];
var titles=temprow.split(/\|/);

i=2;
while (i<numrows){
//for each row

//if not a table bail out

//  get row
temprow = rows[i];
//alert("Row "+i+" = "+temprow);

// split the row into array cell[cols]
var cells=temprow.split(/\|/);
var numcols =cells.length;
var title=cells[1];
try{
var lastspace=title.lastIndexOf(" ");

var res = title.slice(lastspace,title.length); 

// Increment the title if it already exists 

title = $tw.wiki.generateNewTitle(title);

j=2;
//for each colum

while (j<numcols){

if (types[j]=="text"){
temptext=cells[j];

// This line is only used for entries that have pipes
//temptext="{{||ChapterTemplate}}";

}
if (types[j]=="tag") {
tagtext=tagtext+" "+cells[j];
}

++j;
}

//Create new tiddler and put the selected text in it.
	var draftTiddler = new $tw.Tiddler({
			text: temptext,
                        tags:tagtext,
		},
		$tw.wiki.getCreationFields(),
		{
			title: title,
		},$tw.wiki.getModificationFields());
	$tw.wiki.addTiddler(draftTiddler);

//set the fields

j=2;
//for each column

while (j<numcols){
if(types[j]=="field"){

//alert("title = "+title);
this.wiki.setText(title,titles[j],'',cells[j]);
}
++j;

}
} catch(err) {}

//end of row loop
tagtext="";
fieldtext="";
++i;
}

replace=listtext;
operation.replacement=replace;
	operation.cutStart = operation.selStart;
	operation.cutEnd = operation.selEnd;

};

})();
