    "tiddlers": {
        "$:/tesseract/modules/info/platform-extended.js": {
            "text": "/*\
title: $:/core/modules/info/platform-extended.js
type: application/javascript
module-type: info

Initialise extended platform $:/info/ tiddlers

\*/
(function() {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    exports.getInfoTiddlerFields = function() {
        var	infoTiddlerFields = [];
        // Basics

        // Document location
        if ($tw.browser) {
            var setLocationProperty = function(name, value) {
                    infoTiddlerFields.push({
                        title: "$:/info/" + name,
                        text: value
                    });
                }
            setLocationProperty("browserCodeName", navigator.appCodeName);
            setLocationProperty("browserName", navigator.appName);
            setLocationProperty("browserVersion", navigator.appVersion);
            setLocationProperty("cookiesEnabled", navigator.cookieEnabled);
            setLocationProperty("platform", navigator.platform);
            setLocationProperty("user-agentHeader", navigator.userAgent);
            setLocationProperty("browserLanguage", navigator.language);
            setLocationProperty("browserOnline", navigator.onLine);
        }
        return infoTiddlerFields;
    };

})();",
            "type": "application/javascript",
            "title": "$:/tesseract/modules/info/platform-extended.js",
