{
    "tiddlers": {
        "$:/plugins/tesseract/forloop/forloop.js": {
            "text": "/*\
title: $:/plugins/tesseract/forloop/forloop.js
type: application/javascript
module-type: macro

Macro to create forloop
\*/
(function() {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    /*
    Information about this macro
    */

    exports.name = "for";

    exports.params = [{
        name: "text"
    }, {
        name: "forvar"
    }, {
        name: "initval"
    }, {
        name: "maxval"
    }, {
        name: "iter"
    }, {
        name: "joiner"
}];

    /*
    Run the macro
    */
    exports.run = function(text, forvar, initval, maxval, iter, joiner) {
        var textBuffer = [],
            i = parseInt(initval) || 0,
            max = parseInt(maxval) || 10,
            iter = parseInt(iter) || 1,
            forvar=forvar || "$undefined$";
        for (; i <= max; i += iter) {
            textBuffer.push(text.replace(forvar, i));
        }

        return textBuffer.join( joiner || '<br/>');

    }
})();",
            "bag": "default",
            "revision": "0",
            "type": "application/javascript",
            "title": "$:/plugins/tesseract/forloop/forloop.js",
