{
    "tiddlers": {
        "$:/tesseract/modules/filters/sortan.js": {
            "text": "/*\
title: $:/tesseract/modules/filters/sortan.js
type: application/javascript
module-type: filteroperator

Filter operator for sorting alpha-numerically

\*/
(function() {

	/*jslint node: true, browser: true */
	/*global $tw: false */
	"use strict";

	/*
	Export our filter function
	*/
	exports.sortan = function(source, operator, options) {
		var results = prepare_results(source);
		sortTiddlers(results, operator.operand || "title", operator.prefix === "!", false);
		return results;
	};

	exports.sortancs = function(source, operator, options) {
		var results = prepare_results(source);
		sortTiddlers(results, operator.operand || "title", operator.prefix === "!", true);
		return results;
	};



	var prepare_results = function(source) {
		var results = [];
		source(function(tiddler, title) {
			results.push(title);
		});
		return results;
	};


	var sortTiddlers = function(titles, sortField, isDescending, isCaseSensitive) {
		var self = this;
		titles.sort(function(a, b) {
				if (sortField !== "title") {
				var tiddlerA = self.getTiddler(a),
					tiddlerB = self.getTiddler(b);
				if (tiddlerA) {
					a = tiddlerA.fields[sortField] || "";
				} else {
					a = "";
				}
				if (tiddlerB) {
					b = tiddlerB.fields[sortField] || "";
				} else {
					b = "";
				}
			}

			if (isCaseSensitive) {
				return isDescending ? b.localeCompare(a, undefined, {numeric: true, sensitivity: 'variant'}) : a.localeCompare(b, undefined, {numeric: true, sensitivity: 'variant'});
			}
			else{
				return isDescending ? b.localeCompare(a, undefined, {numeric: true, sensitivity: 'base'}) : a.localeCompare(b, undefined, {numeric: true, sensitivity: 'base'});

			}

		});
	};
})();",
            "bag": "default",
            "revision": "1",
            "type": "application/javascript",
            "title": "$:/tesseract/modules/filters/sortan.js",
            "tags": "",
            "module-type": "filteroperator",
            "modified": "20180221124230183",
            "created": "20180221121922241"
        },
        "$:/plugins/tesseract/sortan/Readme": {
            "created": "20180221124801306",
            "text": "<table class="doc-table before-tiddler-body">
<tr><th align="left">purpose</th><td>sort the input alphanumerically</td></tr>
<tr><th align="left">input</th><td>a selection of titles</td></tr>
<tr><th align="left"><a class="tc-tiddlylink tc-tiddlylink-resolves" href="#Filter%20Parameter">parameter</a></th><td><code class="doc-place">F</code> = the name of a <a class="tc-tiddlylink tc-tiddlylink-resolves" href="#TiddlerFields">field</a>, defaulting to <code class="doc-field">title</code></td></tr>
<tr><th align="left">output</th><td>the input, sorted into ascending order by field <code class="doc-field">F</code>, treating field values alphanumerically</td></tr>
<tr><th align="left"><code>!</code> output</th><td>the input, likewise sorted into descending order</td></tr>
</table>


Use ''sortancs'' for case sensitive option",
            "title": "$:/plugins/tesseract/sortan/Readme",
            "tags": "",
            "modified": "20180221132350557"
        }
    }
}
