        "$:/plugins/tg/bottom/button": {
            "created": "20170619101312733",
            "text": "<style>\na.bottom:link {\n    color: red;\n    text-decoration: underline;\n}\na.bottom:visited {\n    color: red;\n}\na.bottom:hover {\n    color: blue;\n}\n</style>\n<a class=\"bottom\" href=\"#bottom\" title=\"Go to bottom\">&darr;</a>\n\n",
            "title": "$:/plugins/tg/bottom/button",
            "tags": "$:/tags/EditToolbar",
            "modifier": "TonGerner",
            "modified": "20200125105832459",
            "creator": "TonGerner",
            "caption": "&darr; go to bottom",
            "description": "Go to bottom of tiddler"
        },
        "$:/plugins/tg/bottom/icon": {
            "created": "20180123194933910",
            "text": "<svg width=\"22pt\" height=\"22pt\" viewBox=\"0 0 100 100\">\n  <path d=\"m48.98237,97.86909l-41.98313,-24.239l0,-48.47796l41.98313,-24.23897l41.98313,24.23897l0,48.47796l-41.98313,24.239z\" stroke-miterlimit=\"4\" stroke-width=\"1.2218\" stroke=\"#a0a0ff\" fill=\"#a0a0ff\"/>\n  <rect fill=\"#000000\" id=\"rect4200\" width=\"8.9372\" height=\"56.28019\" x=\"44.68599\" y=\"11.11111\"/>\n  <rect fill=\"#000000\" id=\"rect4202\" width=\"46.25604\" height=\"2.41546\" x=\"27.77778\" y=\"78.74397\"/>\n  <path fill=\"#000000\" id=\"path4216\" d=\"m49.01451,74.88308l-15.32185,-26.33611l30.79344,0.0253l-15.47158,26.31081z\"/>\n</svg>",
            "title": "$:/plugins/tg/bottom/icon",
            "tags": "",
