{
    "tiddlers": {
        "$:/themes/jd/Alone/Stylesheet": {
            "created": "20180626121537694",
            "creator": "jd",
            "text": "body.tc-body {
    background: #f2f2f2;
}

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
html .tc-page-container {
	text-align: center;
}

html .tc-story-river {
	position: relative;
	width: 770px;
	padding: 42px;
	margin: 0 auto;
	text-align: left;
}

html .tc-sidebar-scrollable {
	text-align: left;
	left: 50%;
	right: 0;
	margin-left: 343px;
}
}

.tc-tiddler-frame {
    border-radius: 2px;
    <<box-shadow "1px 1px 5px rgba(0, 0, 0, 0.3)">>
}

h1.tc-site-title {
    font-family: "Waiting for the Sunrise";
    align-text: center;
    color: #080808;
    margin-bottom: 1.5em;
}

.tc-site-subtitle {
    font-family: monospace;
    color: #080808;
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
h1.tc-site-title {
      margin-top: 1.5em;
}
}

h2.tc-title { font-size: 18px; }

.tc-advanced-search .tc-search { display: none; }

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
.wrapper {
    display: flex;
    justify-content: space-between;
}
.wrapper-baby {
    width: 49%;
}
}

.wrapper .tc-advanced-search .tc-menu-list-item { white-space: normal; }

.wrapper .tc-advanced-search .tc-menu-list-item::before { 
    content: "&#9866; ";
}
.wrapper .tc-btn-invisible { margin-left: 1em; }

input.hyakupa { width: 100%; padding: 4px 8px;}

.wrapper .tc-tab-content { max-height: 28em; overflow: auto; }

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
.wrapper div::-webkit-scrollbar { width: 10px;}
.wrapper div::-webkit-scrollbar-track { background: rgba(0,0,0,0.08); border-radius: 2px; }
.wrapper div::-webkit-scrollbar-thumb { background: rgba(0,0,0,0.08); }
.tc-site-subtitle {
    width: {{$:/themes/tiddlywiki/vanilla/metrics/tiddlerwidth}};
}
}

.tc-subtitle { display: none; }

hr {
     border-top: 0.5px dotted <<colour page-background>>; 
     border-bottom: none;
     border-left: none;
     border-right: none;
}

.tc-tiddler-controls, .tc-tiddler-frame.tc-page-controls {
    opacity: 0;
    transition: 400ms opacity;
}

.tc-tiddler-controls:hover, .tc-tiddler-frame.tc-page-controls:hover {
    opacity: 1;
}

 .tc-tiddler-frame.tc-page-controls .tc-page-controls {
    margin-top: -6px;
}

 .tc-tiddler-frame.tc-page-controls > .tc-page-controls .tc-reveal > .tc-btn-invisible {
    font-size: 12px;
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
.tc-tiddler-frame {
    margin: 28px;
}
.tc-site-subtitle {
    padding: 0 28px;
}
}",
