            "text": "\define paletteBase(theme:Light) $:/themes/jd/Material/Palette/Material$theme$Base

\define swColor(color) <div class="color-prev" style="background: $color$;"/>

\define setColor(name,v2,v3,v4,v5,theme,icon:"{{$:/core/images/menu-button}}") 
<$button class="tc-btn-invisible" style="text-align: left;"> $name$
<$action-setfield $tiddler="$:/palette" text=<<paletteBase $theme$>>/>
<$action-setfield $tiddler="$:/themes/jd/Material/ConfigDB" $index="color-primary" $value=$v2$/>
<$action-setfield $tiddler="$:/themes/jd/Material/ConfigDB" $index="color-sidebar-tab-background" $value=$v3$/>
<$action-setfield $tiddler="$:/themes/jd/Material/ConfigDB" $index="color-button-background" $value=$v4$/>
<$action-setfield $tiddler="$:/themes/jd/Material/ConfigDB" $index="color-tag-background" $value=$v5$/>
<$action-setfield $tiddler="$:/themes/jd/Material/ConfigDB" $index="icon" $value="$icon$"/>
<div>
<$macrocall $name="swColor" color=$v2$/>
<$macrocall $name="swColor" color=$v3$/>
<$macrocall $name="swColor" color=$v4$/>
<$macrocall $name="swColor" color=$v5$/>
</div>
</$button> 
\end

