<<setColor "Lumpy space princess" #7b1fa2 #ae52d4 #e57373 #fdd835 $theme$>>
<<setColor "The bends" #37474f #62727b #bdbdbd #bdbdbd $theme$>>
<<setColor "BMNoir" #212121 #424242 #aeaeae  #aeaeae $theme$ "{{$:/themes/jd/Material/images/bmnoir}}">>
<<setColor "Pet sounds" #f9a825 #fbc02d #4dd0e1 #4dd0e1 $theme$>>
