    "tiddlers": {
        "$:/themes/jd/Mono/TopBarSegments/search": {
            "created": "20190130163600391",
            "creator": "jd",
            "text": "\define NewTidActions(searcharea)
<$action-createtiddler $basetitle={{$searcharea$}} $savetitle="$:/temp/NewTidTitle"/>
<$action-sendmessage $message="tm-edit-tiddler" $param={{$:/temp/NewTidTitle}}/>
<$action-deletetiddler $tiddler="$:/temp/NewTidTitle"/>
<$action-setfield $tiddler="$searcharea$" text=""/>
\end
\define searchbar()
<$list variable="stateCheck" filter="[[$:/config/TopRightBar/$:/themes/jd/Mono/TopBarSegments/search]!text[hide]]">
<div class="tc-sidebar-lists">

<$set name="searchTiddler" value="$:/temp/advancedsearch">
<$keyboard class="tc-search" tag="div" key="enter" actions=<<NewTidActions "$:/temp/advancedsearch">>>
<$edit-text tiddler="$:/temp/advancedsearch" type="search" tag="input" focus={{$:/config/Search/AutoFocus}} focusPopup=<<qualify "$:/state/popup/search-dropdown">> class="tc-popup-handle" default="" placeholder="search, [enter] to create"/>
<$reveal state="$:/temp/advancedsearch" type="nomatch" text="">
<$button tooltip="Create new tiddler with this title" class="tc-btn-invisible" actions=<<NewTidActions "$:/temp/advancedsearch">>>
{{$:/core/images/new-button}}
</$button>
<$button class="tc-btn-invisible">
<$action-setfield $tiddler="$:/temp/advancedsearch" text="" />
{{$:/core/images/close-button}}
</$button>
<$button popup=<<qualify "$:/state/popup/search-dropdown">> class="tc-btn-invisible">
{{$:/core/images/down-arrow}}
</$button>
<$list filter="[is[system]prefix[$:/state/searchtab]text[$:/core/ui/AdvancedSearch/Filter]limit[1]]" variable="tabCheck">
<$list filter="[all[shadows+tiddlers]tag[$:/tags/AdvancedSearch/FilterButton]!has[draft.of]]-$:/core/ui/AdvancedSearch/Filter/FilterButtons/clear"><$transclude/></$list></$list>
</$reveal>
</$keyboard>

<$reveal tag="div" class="tc-block-dropdown-wrapper" state="$:/temp/advancedsearch" type="nomatch" text="">

<$reveal tag="div" class="tc-block-dropdown tc-search-drop-down tc-popup-handle" state=<<qualify "$:/state/popup/search-dropdown">> type="nomatch" text="" default="">

<style>.tc-block-dropdown.tc-search-drop-down { display: flex; }</style>
<<tabs "[all[shadows+tiddlers]tag[$:/tags/AdvancedSearch]!has[draft.of]]" "$:/core/ui/AdvancedSearch/System" "$:/state/searchtab">>

</$reveal>

</$reveal>

</$set>

</div>

</$list>

\end

